from pathlib import PathROOT = Path(__file__).resolve().parents[1]# Path to the star-schema databaseDB_PATH = ROOT / "db" / "nysed.sqlite"# For now, only test the star schemaDBS = {    "star": DB_PATH,    # add "baseline1" back later if needed}# TODO: set this after inspecting dim_year# Example: if SELECT * FROM dim_year shows that year_key 2024 corresponds# to the 2023-24 school year, then:YEAR_KEY_2024 = 2024          # <- replace with the correct key# or, if you prefer to filter by label instead:YEAR_LABEL_2024 = "2023-24"   # <- replace with exact label from dim_yearQUERIES = {    # ELA rate by district (2024)    "ela_district_rate_2024": f"""        SELECT d.district_name,               AVG(fa.qual_rate) AS ela_rate        FROM fact_assessment fa        JOIN dim_year    y   ON y.year_key     = fa.year_key        JOIN dim_subject sub ON sub.subject_key = fa.subject_key        JOIN dim_school  sch ON sch.school_key  = fa.school_key        JOIN dim_district d  ON d.district_key  = sch.district_key        WHERE y.year_key = {YEAR_KEY_2024}          AND sub.subject_id = 'ELA'        GROUP BY d.district_name        ORDER BY d.district_name;    """,    # Chronic absence vs ELA rate (2024), by district    "absence_vs_ela_2024": f"""        SELECT d.district_name,               AVG(at.absence_rate) AS chronic_abs,               AVG(fa.qual_rate)    AS ela_rate        FROM fact_assessment fa        JOIN fact_attendance at          ON at.year_key      = fa.year_key         AND at.school_key    = fa.school_key         AND at.subgroup_key  = fa.subgroup_key        JOIN dim_year    y   ON y.year_key     = fa.year_key        JOIN dim_subject sub ON sub.subject_key = fa.subject_key        JOIN dim_school  sch ON sch.school_key  = fa.school_key        JOIN dim_district d  ON d.district_key  = sch.district_key        WHERE y.year_key = {YEAR_KEY_2024}          AND sub.subject_id = 'ELA'        GROUP BY d.district_name        ORDER BY d.district_name;    """,}# Wide-table queries are not needed right now, but keep the dict so# time_queries.py still imports without error; you simply won't use DBS["baseline1"].QUERIES_WIDE = {}